'''importing all esesntail Qtmodules'''from PyQt4.QtCore import *from PyQt4.QtGui import *import sysclass hw(QDialog):    '''defining a class to iniate desktop app    body and its content'''    def __init__(self):        '''constructor for hw class'''        QDialog.__init__(self)  # inherits QDialogs constructor        layout = QGridLayout()  # defining grid layout format for deskapp        label = QLabel("Hellow World!")  # defining labels or static text to be displayed        line_edit = QLineEdit()  # creating a text entry field        label2 = QLabel("")        button = QPushButton("Close")  # adding a push button        # adding checkbox        self.checkbox = QCheckBox()        self.checkbox.setText("check here")        # pushing all objects as widgets on the app layout        layout.addWidget(label, 0, 0)        layout.addWidget(line_edit, 1, 0)        layout.addWidget(label2, 1, 1)        layout.addWidget(self.checkbox, 2, 0)        layout.addWidget(button, 2, 1)        #adding events        self.setLayout(layout)        button.clicked.connect(self.close)        line_edit.textChanged.connect(label2.setText)        self.checkbox.stateChanged.connect(self.selected)    def selected(self):        if self.checkbox.isChecked():            self.checkbox.setText("nice!")        else:            self.checkbox.setText("check here")app = QApplication(sys.argv)dialog = hw()dialog.show()sys.exit(app.exec_())